import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';

function ReptileList() {
  const countries = ['france', 'italie', 'espagne'];
  const nb = 30;
  return (
    <ol>
      {countries.map(reptile => (
        //<li key={reptile}>
        <button className="button">
          {reptile}: {nb}
        </button>
        //</li>
      ))}
    </ol>
  );
}

function App() {

  const [test, setTest] = useState(600);
  const [death, setDeath] = useState(600);
  const [rec, setRec] = useState(600);

  function changetest() {
    setTest(test + 3)
    console.log('The link was clicked.');
  }

  return (
    <div className="App">
    <header className="Back-header">
    </header>
      <header className="Total-header">
        <p>
            Total case:<br />
            {test}
        </p>
      </header>
      <header className="Death-header">
        <p>
            Total deaths:<br />
            {death}
        </p>
      </header>
      <header className="Rec-header">
        <p>
            Total recovered:<br />
            {rec}
        </p>
      </header>
      <header className="List-header">
        <p>
            {ReptileList()}
        </p>
      </header>
      <header className="Map-header">
        <p>
            Ceci est une <br/>
            carte <br/>
            SI SI C UNE CARTE FRERO
        </p>
        <button onClick={changetest}>
          Activate Lasers
        </button>
      </header>
      <header className="Chart-header">
        <p>
            le joli graphique
        </p>
      </header>
    </div>
  );
}

export default App;
